function showPic(a){var b=a.getAttribute("href");var e=document.getElementById("placeholder");e.setAttribute("src",b);var d=a.getAttribute("title");var c=document.getElementById("desc");c.firstChild.nodeValue=d}function prepareGallery(){if(!document.getElementByTagName){return false}if(!document.getElementId){return false}if(!document.getElementById("imagegallery")){return false}var a=document.getElementById("imagegallery");var b=a.getElementsByTagName("a");for(var c=0;c<b.length;c++){b[c].onclick=function(){showPic(this);return false}}}window.onload=prepareGallery;